import "./Product.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faHeart } from '@fortawesome/free-solid-svg-icons';
import { faTrash } from '@fortawesome/free-solid-svg-icons';
export default function Product({ photo, title, description, price }) {
  const [iconColor, setIconColor] = useState('black');

  // Tıklama fonksiyonu: İkonun rengini kırmızı yapar, eğer zaten kırmızıysa siyah yapar
  const handleIconClick = () => {
    setIconColor(prevColor => (prevColor === 'black' ? 'red' : 'black'));
  };
  return (
    <div className="product-cart">
      <FontAwesomeIcon icon={faHeart} className="like" onClick={handleIconClick} />
      <FontAwesomeIcon icon={faTrash} className="trash"/>
      <img src={photo} className="image" />
      <div className="product-information">
        <h2 className="product-name">{title}</h2>
        <p className="price">{price} USD</p>
        <button className="more-information">Learn More</button>
      </div>
    </div>
  );
}
