import "./Checkout.css";
import { useSelector, useDispatch } from "react-redux";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTrash } from "@fortawesome/free-solid-svg-icons";
import { faHeart } from "@fortawesome/free-solid-svg-icons";
import { removefromCart } from "./redux/cartSlice";

function Checkout() {
  let cartitems = useSelector((state) => state.cart.cart);
  let dispatch = useDispatch();
  const handleDelete = (id) => {
    dispatch(removeProduct(id));
  };
  return (
    <div className=" checkout">
      <h2>CheckOut</h2>
      <div className="product-list-item">
        {cartitems.map((products) => {
          return (
            <div className="product-cart-item">
              <button className="like">
                <FontAwesomeIcon icon={faHeart} />
              </button>
              <button
                className="trash"
                onClick={() => handleDelete(products.id)}
              >
                <FontAwesomeIcon icon={faTrash} />
              </button>
              <img src={products.photo} className="image" />
              <div className="product-information">
                <h2 className="product-name">{products.title}</h2>
                <p className="price">{products.price} USD</p>
                <button className="more-information">Learn More</button>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
}
export default Checkout;
