import React from "react";
import { useSelector, useDispatch } from "react-redux";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTrash, faHeart } from "@fortawesome/free-solid-svg-icons";
import { removefromCart } from "../redux/cartSlice";

function Checkout() {
  let cartitems = useSelector((state) => state.cart.cart);
  let dispatch = useDispatch();
  
  return (
    <div className="checkout">
      <h2>CheckOut</h2>
      <div className="product-list-item">
        {cartitems.map((product) => (
          <div key={product.id} className="product-cart-item">
            <button className="like">
              <FontAwesomeIcon icon={faHeart} />
            </button>
            <button
              className="trash"
              onClick={() => dispatch(removefromCart({ id: product.id }))}
            >
              <FontAwesomeIcon icon={faTrash} />
            </button>
            <img src={product.photo} className="image" alt={product.title} />
            <div className="product-information">
              <h2 className="product-name">{product.title}</h2>
              <p className="price">{product.price} USD</p>
              <button className="more-information">Learn More</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Checkout;
