import "./Product.css";
import React, { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faHeart } from "@fortawesome/free-solid-svg-icons";
import { faTrash } from "@fortawesome/free-solid-svg-icons";
import { useSelector, useDispatch } from "react-redux";
import { removeProduct } from "./products/productsSlice";
export default function Product({ photo, title, description, price, id }) {
  let [iconColor, setIconColor] = useState("black");
  let [handleClose] = useState();

  let handleIconClick = () => {
    setIconColor((prevColor) => (prevColor === "black" ? "red" : "black"));
  };
  const ProductList = () => {
    const products = useSelector((state) => state.products);
    const dispatch = useDispatch();

    const handleRemove = (id) => {
      dispatch(removeProduct(id));
    };
    return (
      <div className="product-cart">
        <FontAwesomeIcon
          icon={faHeart}
          className="like"
          onClick={handleIconClick}
          style={{ color: iconColor }}
        />{" "}
        <button onClick={() => handleRemove(products.id)}>
          {" "}
          <FontAwesomeIcon
            icon={faTrash}
            className="trash"
            onClick={handleClose}
          />
        </button>
        <img src={photo} className="image" />
        <div className="product-information">
          <h2 className="product-name">{title}</h2>
          <p className="price">{price} USD</p>
          <button className="more-information">Learn More</button>
        </div>
      </div>
    );
  };
}
